mapper.select:
select state from t_coupon_instance
where user_id = #{userId} and coupon_id = #{couponId}
for update

mapper.update:
update t_coupon_instance
set state = #{state}, use_time = #{useTime}, order_id = #{orderId}
where user_id = #{userId} and coupon_id = #{couponId}

update t_coupon_instance
set state = #{state}
where user_id = #{userId} and coupon_id = #{couponId}
